html{
    --primary: #AD8762;
    --secondary: #CFAF8F;
    --tertiary: #FAE3CC;
    --complimentary: #485275;
    --border: #000000;
    --shadow: #000000;
    --font: #000000;
    --field: ghostwhite;
}

* {
    box-sizing: border-box;
    padding: 0;
    margin: 0;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

@mixin disable-selection {
    -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none;   /* Safari */
    -khtml-user-select: none;    /* Konqueror HTML */
    -moz-user-select: none;      /* Firefox */
    -ms-user-select: none;       /* Internet Explorer/Edge */
    user-select: none;           /* Non-prefixed version, currently supported by Chrome and Opera */
}

body {
    height: 100vh;
    width: 100vw;
    color: var(--font);
    #app{
        width: 100%;
        height: 100%;
        .app {
            display: flex;
            flex-flow: column;
            width: 100%;
            height: 100%;
            &__header{
                display: flex;
                justify-content: space-between;
                align-items: center;
                height: 15%;
                width: 100%;
                border: solid var(--border);
                background-color: var(--primary);
                &--name{
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    font-size: calc(1vw + 5vh);
                    font-family: 'Montserrat', sans-serif;
                    text-shadow: 0 2px 5px var(--shadow);
                    @include disable-selection;
                    width: 30%;
                    height: 100%;
                    &:hover{
                        cursor: pointer;
                    }
                    &:active{
                        transform: scale(0.99);
                    }
                }
                &--nav{
                    display: flex;
                    justify-content: flex-end;
                    align-items: center;
                    width: 45%;
                    height: 100%;
                    &--left{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        font-size: calc(0.66vw + 3vh);
                        width: 50%;
                        height: 80%;
                        border: solid var(--border);
                        margin-right: 5%;
                        border-radius: 25px;
                        font-weight: bold;
                        box-shadow: 0 2px 5px var(--shadow);
                        &:hover{
                            cursor: pointer;
                            transform: translateY(-2px);
                        }
                    }
                    &--right{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        font-size: calc(0.66vw + 3vh);
                        width: 50%;
                        height: 80%;
                        border: solid var(--border);
                        border-radius: 25px;
                        margin-right: 5%;
                        font-weight: bold;
                        box-shadow: 0 2px 5px var(--shadow);
                        &:hover{
                            cursor: pointer;
                            transform: translateY(-2px);
                        }
                    }
                }
            }
            &__body{
                display: flex;
                flex-flow: column;
                height: 75%;
                width: 100%;
                font-family: 'EB Garamond', serif;
                border: solid var(--border);
                background-color: var(--tertiary);
                &--content{
                    display: flex;
                    flex-flow: row;
                    width: 90%;
                    height: 80%;
                    margin: auto;
                    border: solid var(--border);
                    background-color: var(--secondary);
                    &--input{
                        display: flex;
                        flex-flow: column;
                        justify-content: center;
                        align-items: center;
                        border: solid var(--border);
                        width: 80%;
                        height: 100%;
                        font-size: calc(2vw + 1.5vh);
                        font-weight: bold;
                        letter-spacing: 1px;
                        .top{
                            display: flex;
                            flex-flow: column;
                            justify-content: center;
                            align-items: center;
                            width: 100%;
                            height: 50%;
                            padding-top: calc(1.25vw + 6.25vh);
                        }
                        .bottom{
                            display: flex;
                            flex-flow: column;
                            justify-content: center;
                            align-items: center;
                            width: 100%;
                            height: 50%;
                            padding-bottom: calc(2.5vw + 12.5vh);
                        }
                        .bottom > label, .top > label{
                            display: flex;
                            justify-content: center;
                            width: 80%;
                        }
                        .bottom > input, .top > input{
                            font-size: calc(1vw + .77vh);
                            width: 80%;
                            text-align: center;
                            padding: 2px;
                            letter-spacing: 1px;
                            border: solid var(--border);
                            box-shadow: 0 3px 5px var(--shadow);
                            outline: none;
                            color: var(--font);
                        }
                    }
                    &--track{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        border: solid var(--border);
                        width: 20%;
                        height: 100%;
                        color: var(--font);
                        button{
                            font-size: calc(2vw + 1.5vh);
                            font-family: 'Montserrat', sans-serif;
                            width: 80%;
                            height: 40%;
                            border-radius: 10%;
                            box-shadow: 0 3px 5px var(--shadow);
                            outline: none;
                            background-color: ghostwhite;
                            color: var(--font);
                            .fa-box{
                                color: #964b00;
                            }
                            .fa-user-plus{
                                color: green;
                            }
                            .fa-sign-in-alt{
                                color: blue;
                            }
                            &:hover{
                                cursor: pointer;
                                background-color: lightgrey;
                            }
                        }
                    }
                }
                .package{
                    display: flex;
                    flex-flow: column;
                    width: 100%;
                    height: 100%;
                    overflow: auto;
                    &__tracking-number, &__carrier-code, &__status{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        height: auto;
                        width: 80%;
                        font-size: calc(0.50vw + 2vh);
                        font-weight: bold;
                        letter-spacing: 1px;
                        text-transform: capitalize;
                        margin: 20px auto 0 auto;
                    }
                    &__status{
                        &--icon{
                            .far, .fa-check-circle{
                                display: flex;
                                justify-content: center;
                                align-items: center;
                                font-size: calc(2vw + 1.5vh);
                                height: calc(2vw + 1.5vh);
                                width: 100%;
                                color: green;
                                margin-top: calc(0.33vw + 1.77vh);
                            }
                        }
                    }
                    &__container{
                        display: flex;
                        flex-flow: column;
                        height: auto;
                        width: 100%;
                        font-size: calc(0.33vw + 1.77vh);
                        &--item{
                            display: flex;
                            flex-flow: column;
                            justify-content: center;
                            align-items: center;
                            margin: 20px auto;
                            width: 80%;
                            height: 100%;
                            border: solid var(--border);
                            box-shadow: 0 2px 5px var(--shadow);
                            background-color: var(--secondary);
                            &--checkpoint{
                                display: flex;
                                justify-content: center;
                                text-align: center;
                                width: 80%;
                                text-transform: capitalize;
                                margin: 10px auto;
                                border-bottom: solid var(--border) 1px;
                            }
                            &--location, &--status, &--date{
                                display: flex;
                                justify-content: center;
                                text-align: center;
                                width: 80%;
                                margin: 10px auto;
                                border-bottom: solid var(--border) 1px;
                            }
                        }
                    }
                }
            }
            &__footer{
                display: flex;
                flex-flow: row;
                justify-content: center;
                align-items: center;
                height: 10%;
                width: 100%;
                border: solid var(--border);
                font-size: calc(0.33vw + 1.77vh);
                font-family: 'EB Garamond', serif;
                background-color: var(--primary);
                a{
                    text-align: center;
                    margin: calc(0.17vw + .8vh);
                    font-size: calc(0.33vw + 1.77vh);
                    letter-spacing: 1px;
                    text-decoration: none;
                    color: var(--complimentary);
                    font-weight: bold;
                    &:hover{
                        text-decoration: underline;
                        transform: scale(1.05);
                    }

                }
            }
        }  
    } 
}

.code{
    display: inline-block;
    position: relative;
    text-align: center;
    width: 140px;
    &__content{
        display: none;
        width: 100%;
        z-index: 1;
        a{
            display: inline-block;
            width: 100%;
            border: solid var(--border);
            margin: 0 !important;
        }
    }
    &__a{
        font-size: calc(.7vw + 2.5vh) !important;
    }
    &:hover{
        display: block;
        bottom: 50%;
    }
    &:hover &__content{
        display: block;
        a{
            display: inline-block;
            padding: 2px;
            background-color: var(--tertiary)
        }
    }
}

.center{
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    .loading {
        z-index: 1;
        border-radius: 50%;
        border-top: solid var(--tertiary) 16px;
        border-right: solid var(--border) 16px;
        border-bottom: solid var(--tertiary) 16px;
        border-left: solid var(--border) 16px;
        width: calc(10vw + 10vh);
        height: calc(10vw + 10vh);
        animation: spin 2s linear infinite;
    }    
}
