* {
    box-sizing: border-box;
    padding: 0;
    margin: 0;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

@mixin disable-selection {
    -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none;   /* Safari */
    -khtml-user-select: none;    /* Konqueror HTML */
    -moz-user-select: none;      /* Firefox */
    -ms-user-select: none;       /* Internet Explorer/Edge */
    user-select: none;           /* Non-prefixed version, currently supported by Chrome and Opera */
}

body {
    height: 100vh;
    width: 100vw;
    background-color: ghostwhite;
    #app{
        width: 100%;
        height: 100%;
        .app {
            display: flex;
            flex-flow: column;
            width: 100%;
            height: 100%;
            &__header{
                display: flex;
                justify-content: space-between;
                align-items: center;
                height: 15%;
                width: 100%;
                border: solid black;
                &--name{
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    font-size: calc(1vw + 5vh);
                    margin-left: 20px;
                    @include disable-selection;
                    &:hover{
                        cursor: pointer;
                    }
                    &:active{
                        transform: scale(0.99);
                    }
                }
            }
            &__body{
                display: flex;
                flex-flow: column;
                height: 75%;
                width: 100%;
                &--content{
                    display: flex;
                    flex-flow: row;
                    width: 90%;
                    height: 80%;
                    margin: auto;
                    &--input{
                        display: flex;
                        flex-flow: column;
                        justify-content: center;
                        align-items: center;
                        border: solid black;
                        width: 80%;
                        height: 100%;
                        font-size: calc(2vw + 1.5vh);
                        input{
                            font-size: calc(1vw + .77vh);
                            width: 80%;
                            text-align: center;
                            padding: 2px;
                            letter-spacing: 1px;
                        }
                    }
                    &--track{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        border: solid black;
                        width: 20%;
                        height: 100%;
                        button{
                            font-size: calc(2vw + 1.5vh);
                            width: 80%;
                            height: 35%;
                        }
                    }
                }
                .package{
                    display: flex;
                    flex-flow: column;
                    border: solid black;
                    width: 100%;
                    height: 100%;
                    overflow: auto;
                    &__tracking-number, &__carrier-code, &__status{
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        height: auto;
                        width: 100%;
                        font-size: calc(0.50vw + 2vh);
                        text-transform: capitalize;
                        margin-top: 20px;
                    }
                    &__container{
                        display: flex;
                        flex-flow: column;
                        height: auto;
                        width: 100%;
                        font-size: calc(0.33vw + 1.77vh);
                        &--item{
                            display: flex;
                            flex-flow: column;
                            justify-content: center;
                            align-items: center;
                            margin: 20px auto;
                            width: 80%;
                            height: 100%;
                            border: solid black;
                            &--checkpoint{
                                display: flex;
                                justify-content: center;
                                text-align: center;
                                width: 80%;
                                text-transform: capitalize;
                                margin: 10px auto;
                                border-bottom: solid black 1px;
                            }
                            &--location, &--status, &--date{
                                display: flex;
                                justify-content: center;
                                text-align: center;
                                width: 80%;
                                margin: 10px auto;
                                border-bottom: solid black 1px;
                            }
                        }
                    }
                }
            }
            &__footer{
                display: flex;
                flex-flow: row;
                justify-content: center;
                align-items: center;
                height: 10%;
                width: 100%;
                border: solid black;
                font-size: calc(0.33vw + 1.77vh);
                a{
                    text-align: center;
                    margin: 10px;
                    font-size: calc(0.33vw + 1.77vh);
                    letter-spacing: 1px;
                    text-decoration: none;
                    color: red;
                    font-weight: bold;
                    &:hover{
                        text-decoration: underline;
                        transform: scale(1.05);
                    }

                }
            }
        }  
    } 
}

.code{
    display: inline-block;
    position: relative;
    text-align: center;
    width: 140px;
    &__content{
        display: none;
        background-color: ghostwhite;
        width: 100%;
        z-index: 1;
        a{
            display: inline-block;
            width: 100%;
            border: solid black;
            margin: 0 !important;
        }
    }
    &__a{
        font-size: calc(.7vw + 2.5vh) !important;
    }
    &:hover{
        display: block;
        bottom: 50%;
    }
    &:hover &__content{
        display: block;
        a{
            display: inline-block;
            padding: 2px;

        }
    }
}

.center{
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    .loading {
        z-index: 1;
        border-radius: 50%;
        border-top: solid ghostwhite 16px;
        border-right: solid #000000 16px;
        border-bottom: solid ghostwhite 16px;
        border-left: solid #000000 16px;
        width: calc(10vw + 10vh);
        height: calc(10vw + 10vh);
        animation: spin 2s linear infinite;
    }    
}
